// app/(app)/chat/actions.ts
'use server';

import { auth } from '@/auth.config';
import { prisma } from '@/lib/prisma';
import broker from './sse/broker';
import { redirect } from 'next/navigation';

const toStr = (v: unknown) => (typeof v === 'string' ? v : '');
const now = () => new Date();

function requireSessionId(session: any): string {
  const id = session?.user?.id;
  if (typeof id !== 'string' || !id) redirect('/sign-in');
  return id;
}

/**
 * –û—Ç–ø—Ä–∞–≤–∫–∞ —Å–æ–æ–±—â–µ–Ω–∏—è + –≤–ª–æ–∂–µ–Ω–∏–π (–¥–æ 10 –ú–ë/—Ñ–∞–π–ª), –∞–ø–¥–µ–π—Ç lastMessage, –æ—Ç–º–µ—Ç–∫–∞ –ø—Ä–æ—á—Ç–µ–Ω–∏—è –æ—Ç–ø—Ä–∞–≤–∏—Ç–µ–ª—è.
 * –ë–µ–∑ redirect, –æ–±–Ω–æ–≤–ª–µ–Ω–∏–µ –ø—Ä–∏–¥—ë—Ç —á–µ—Ä–µ–∑ SSE.
 */
export async function sendMessageAction(formData: FormData): Promise<void> {
  const session = await auth();
  const me = requireSessionId(session);

  const threadId = toStr(formData.get('threadId'));
  const text = toStr(formData.get('text')).trim();
  if (!threadId) return;

  const th = await prisma.thread.findFirst({
    where: { id: threadId, OR: [{ aId: me }, { bId: me }] },
    select: { aId: true, bId: true },
  });
  if (!th) return;

  // –ü–æ–¥–≥–æ—Ç–æ–≤–∏–º —Ñ–∞–π–ª—ã (–±–µ–∑ –ø–∞–¥–µ–Ω–∏—è —Ç–∏–ø–æ–≤ –≤ DOM/Node)
  const files = (formData.getAll('files') as any[]).filter(Boolean);
  const blobs: { name: string; mime: string; size: number; data: Buffer }[] = [];
  for (const f of files) {
    // –ø—Ä–æ–≤–µ—Ä—è–µ–º –ø–æ ¬´—É—Ç–∏–Ω—ã–º¬ª –ø—Ä–∏–∑–Ω–∞–∫–∞–º, —á—Ç–æ–±—ã TS –Ω–µ —Ä—É–≥–∞–ª—Å—è
    if (f && typeof f.arrayBuffer === 'function' && typeof f.size === 'number') {
      if (f.size <= 10 * 1024 * 1024) {
        const buf = Buffer.from(await f.arrayBuffer());
        blobs.push({
          name: typeof f.name === 'string' && f.name ? f.name : 'file',
          mime: typeof f.type === 'string' && f.type ? f.type : 'application/octet-stream',
          size: f.size,
          data: buf,
        });
      }
    }
  }

  if (!text && blobs.length === 0) return;

  // –°—Ö–ª–æ–ø—ã–≤–∞–µ–º –≤ –æ–¥–Ω—É —Ç—Ä–∞–Ω–∑–∞–∫—Ü–∏—é
  await prisma.$transaction(async (tx) => {
    const msg = await tx.message.create({
      data: { authorId: me, threadId, text: text || '' },
      select: { id: true },
    });

    if (blobs.length) {
      await tx.attachment.createMany({
        data: blobs.map((b) => ({
          messageId: msg.id,
          name: b.name,
          mime: b.mime,
          size: b.size,
          data: b.data,
        })),
      });
    }

    await tx.thread.update({
      where: { id: threadId },
      data: { lastMessageAt: now(), lastMessageText: text || (blobs[0]?.name ?? '–≤–ª–æ–∂–µ–Ω–∏–µ') },
    });

    await tx.readMark.upsert({
      where: { threadId_userId: { threadId, userId: me } },
      update: { readAt: now() },
      create: { threadId, userId: me, readAt: now() },
    });
  });

  // –°–æ–±—ã—Ç–∏–µ ‚Äî –æ–±–µ–∏–º —Å—Ç–æ—Ä–æ–Ω–∞–º; authorId –æ–ø—Ü–∏–æ–Ω–∞–ª–µ–Ω, –Ω–æ –ø–æ–ª–µ–∑–µ–Ω –¥–ª—è –∫–ª–∏–µ–Ω—Ç–∞
  broker.publish([th.aId, th.bId], { type: 'message', threadId, at: Date.now(), authorId: me });
}

/** –£–¥–∞–ª–µ–Ω–∏–µ –¥–∏–∞–ª–æ–≥–∞ —Ü–µ–ª–∏–∫–æ–º (—Å–æ–æ–±—â–µ–Ω–∏—è, –≤–ª–æ–∂–µ–Ω–∏—è, –æ—Ç–º–µ—Ç–∫–∏) + —Å–æ–±—ã—Ç–∏–µ + redirect –æ–±—Ä–∞—Ç–Ω–æ –≤ —Å–ø–∏—Å–æ–∫ */
export async function deleteThreadAction(formData: FormData): Promise<void> {
  const session = await auth();
  const me = requireSessionId(session);

  const threadId = toStr(formData.get('threadId'));
  if (!threadId) return redirect('/chat');

  const th = await prisma.thread.findFirst({
    where: { id: threadId, OR: [{ aId: me }, { bId: me }] },
    select: { aId: true, bId: true },
  });
  if (!th) return redirect('/chat');

  await prisma.$transaction(async (tx) => {
    const ids = await tx.message.findMany({ where: { threadId }, select: { id: true } });
    await tx.attachment.deleteMany({ where: { messageId: { in: ids.map((i) => i.id) } } });
    await tx.message.deleteMany({ where: { threadId } });
    await tx.readMark.deleteMany({ where: { threadId } });
    await tx.thread.delete({ where: { id: threadId } });
  });

  broker.publish([th.aId, th.bId], { type: 'threadDeleted', threadId, at: Date.now() });
  redirect('/chat');
}

'use client';
import { useEffect, useRef } from 'react';
import { useRouter } from 'next/navigation';

export default function Live({ uid, activeThreadId }: { uid: string; activeThreadId?: string }) {
  const router = useRouter();
  const esRef = useRef<EventSource | null>(null);
  const last = useRef(0);

  useEffect(() => {
    let stop = false;

    const connect = () => {
      if (stop) return;
      esRef.current?.close();
      const es = new EventSource(`/chat/sse?uid=${encodeURIComponent(uid)}`);
      esRef.current = es;

      es.onmessage = (e) => {
        try {
          const data = JSON.parse(e.data || '{}') as { type: string; threadId?: string };
          // –º—è–≥–∫–∏–π refresh —Ç–æ–ª—å–∫–æ –µ—Å–ª–∏ —ç—Ç–æ –ø—Ä–æ –∞–∫—Ç–∏–≤–Ω—ã–π —Ç—Ä–µ–¥ –ª–∏–±–æ —Å–æ–±—ã—Ç–∏–µ —Å–æ–∑–¥–∞–Ω–∏—è/—É–¥–∞–ª–µ–Ω–∏—è
          if (
            data.type === 'threadCreated' ||
            data.type === 'threadDeleted' ||
            (data.type === 'message' && data.threadId === activeThreadId) ||
            (data.type === 'read'     && data.threadId === activeThreadId)
          ) {
            const now = Date.now();
            if (now - last.current > 250) {
              last.current = now;
              router.refresh();
            }
          } else {
            // –¥–ª—è –±–µ–π–¥–∂–∞ –≤ —Å–∞–π–¥–±–∞—Ä–µ —Ç–æ–∂–µ –º–æ–∂–Ω–æ –¥–µ—Ä–Ω—É—Ç—å —Ä–µ–¥–∫–∏–π refresh
            const now = Date.now();
            if (now - last.current > 900) {
              last.current = now;
              router.refresh();
            }
          }
        } catch {}
      };

      es.onerror = () => {
        es.close();
        if (!stop) setTimeout(connect, 1200);
      };
    };

    connect();
    return () => { stop = true; esRef.current?.close(); };
  }, [router, uid, activeThreadId]);

  return null;
}

