import type { ReactNode } from 'react';
import { auth } from '@/auth.config';
import { prisma } from '@/lib/prisma';
import { Prisma } from '@prisma/client';
import Sidebar from '@/components/Sidebar';
import Heartbeat from './heartbeat/Heartbeat';
import { heartbeat } from './heartbeat/actions';

async function unreadTotal(uid: string) {
  const threads = await prisma.thread.findMany({
    where: { OR: [{ aId: uid }, { bId: uid }] },
    select: { id: true },
  });
  const ids = threads.map(t => t.id);
  if (ids.length === 0) return 0;

  const rows = await prisma.$queryRaw<{ count: bigint }[]>`
    SELECT COUNT(*)::bigint AS count
    FROM "Message" m
    LEFT JOIN "ReadMark" r
      ON r."threadId" = m."threadId" AND r."userId" = ${uid}
    WHERE m."threadId" IN (${Prisma.join(ids)})
      AND (r."readAt" IS NULL OR m."createdAt" > r."readAt")
      AND m."authorId" <> ${uid}
  `;
  return Number(rows[0]?.count ?? 0);
}

export default async function AppLayout({ children }: { children: ReactNode }) {
  const session = await auth();
  const uid = (session?.user as any)?.id as string | undefined;
  const unread = uid ? await unreadTotal(uid) : 0;

  return (
    <div style={{ display:'grid', gridTemplateColumns:'280px 1fr', minHeight:'100vh' }}>
      <Sidebar unreadChats={unread} />
      <main style={{ padding:12 }}>
        {children}
        <Heartbeat action={heartbeat} />
      </main>
    </div>
  );
}

'use client';

import { usePathname, useRouter, useSearchParams } from 'next/navigation';
import { useEffect, useMemo, useRef, useState } from 'react';

export default function SearchBox({ initial }: { initial?: string }) {
  const router = useRouter();
  const pathname = usePathname();
  const sp = useSearchParams();
  const [q, setQ] = useState(initial ?? '');

  const base = useMemo(() => {
    const obj = Object.fromEntries(sp?.entries() ?? []);
    delete obj.ok; delete obj.error;
    return obj;
  }, [sp]);

  // debounce + IME-guard (–Ω–µ —Ç—Ä–∏–≥–≥–µ—Ä–∏–º –≤–æ –≤—Ä–µ–º—è –Ω–∞–±–æ—Ä–∞ –ø–æ —Ä–∞—Å–∫–ª–∞–¥–∫–µ/IME)
  const timerRef = useRef<number | null>(null);
  const composingRef = useRef(false);

  useEffect(() => { setQ(initial ?? ''); }, [initial]);

  function push(qNext: string) {
    const params = new URLSearchParams(base);
    if (qNext) params.set('q', qNext); else params.delete('q');
    router.replace(params.size ? `${pathname}?${params.toString()}` : pathname);
  }

  function schedule(qNext: string) {
    if (timerRef.current) window.clearTimeout(timerRef.current);
    // —Ç–æ–ª—å–∫–æ –µ—Å–ª–∏ –ø—É—Å—Ç–æ –∏–ª–∏ 2+ —Å–∏–º–≤–æ–ª–∞ ‚Äî —á—Ç–æ–±—ã –Ω–µ –¥–µ—Ä–≥–∞—Ç—å —Å–µ—Ä–≤–µ—Ä –∫–∞–∂–¥—É—é –±—É–∫–≤—É
    const shouldUpdate = qNext.length === 0 || qNext.length >= 2;
    timerRef.current = window.setTimeout(() => {
      if (!composingRef.current && shouldUpdate) push(qNext);
    }, 450);
  }

  return (
    <input
      value={q}
      onChange={(e) => { const v = e.target.value; setQ(v); schedule(v); }}
      onCompositionStart={() => { composingRef.current = true; }}
      onCompositionEnd={(e) => { composingRef.current = false; schedule((e.target as HTMLInputElement).value); }}
      onBlur={(e) => push(e.currentTarget.value)}         // –º–≥–Ω–æ–≤–µ–Ω–Ω–æ –ø–æ blur
      onKeyDown={(e) => { if (e.key === 'Enter') push(q); }} // –º–≥–Ω–æ–≤–µ–Ω–Ω–æ –ø–æ Enter
      placeholder="–ø–æ–∏—Å–∫: —Ñ–∏–æ, email, —Ç–µ–ª–µ—Ñ–æ–Ω, –∫–ª–∞—Å—Å–Ω–æ–µ —Ä—É–∫–æ–≤–æ–¥—Å—Ç–≤–æ, –ª–æ–≥–∏–Ω, —Ä–æ–ª—å‚Ä¶"
      style={{
        height: 36,
        fontSize: 14,
        width: 520,
        maxWidth: '60ch',
        padding: '6px 10px',
        borderRadius: 10,
        border: '1px solid rgba(229,231,235,.9)',
        background: '#fff',
        outline: 'none',
      }}
    />
  );
}

