  user?: { id: string; name: string | null } | null;
};

type Task = {
  id: string;
  title: string;
  description: string | null;
  dueDate: string; // ISO
  hidden: boolean;
  priority: 'normal' | 'high';
  createdById: string | null;
  createdByName: string | null;
  assignees: Assignee[];
};

