  const task = taskId ? mapById.get(taskId) ?? null : null;
  const dayList = dayIso ? (tasksByDay.get(dayIso) ?? []) : [];

  // –ü–æ—Ä—Ç–∞–ª –¥–ª—è –º–æ–¥–∞–ª–æ–∫
