export const dynamic = 'force-dynamic';
export const revalidate = 0;

function hasAccess(role: Role | null | undefined) {
  return role === 'director' || role === 'deputy_plus';
}

function pickStr(v: string | string[] | undefined): string | undefined {
  if (Array.isArray(v)) return v[0];
  if (typeof v === 'string' && v.trim() !== '') return v;
  return undefined;
}

export default async function Page({
  searchParams,
}: {
  searchParams: Promise<Record<string, string | string[] | undefined>>;
}) {
  const sp = await searchParams;
  const qg = pickStr(sp.qg);
  const qs = pickStr(sp.qs);
  const qu = pickStr(sp.qu);

  const session = await auth();
  const role = normalizeRole((session?.user as any)?.role ?? null);
  if (!hasAccess(role)) redirect('/');

  const [users, groups, subjects] = await Promise.all([
    prisma.user.findMany({
      where: qu
        ? { name: { contains: qu, mode: 'insensitive' } }
        : undefined,
      select: { id: true, name: true, role: true },
      orderBy: [{ name: 'asc' }],
    }),
    prisma.group.findMany({
      where: qg
        ? { name: { contains: qg, mode: 'insensitive' } }
        : undefined,
      select: { id: true, name: true },
      orderBy: [{ name: 'asc' }],
    }),
    prisma.subject.findMany({
      where: qs
        ? { name: { contains: qs, mode: 'insensitive' } }
        : undefined,
      select: { id: true, name: true, _count: { select: { members: true } } },
      orderBy: [{ name: 'asc' }],
    }),
  ]);

  const initialSubjects = subjects.map((s) => ({
    id: s.id,
    name: s.name,
    count: s._count.members,
  }));

  return (
    <main style={{ padding: 14 }}>
      <GroupsBoard
        initialUsers={users}
        initialGroups={groups}
        initialSubjects={initialSubjects}
        subjectsEnabled
      />
    </main>
  );
}

'use client';

import { useEffect, useRef } from 'react';
import { usePathname } from 'next/navigation';

export default function Heartbeat({
  action,
  intervalMs = 600_000,     // —Ä–∞–∑ –≤ 10 –º–∏–Ω—É—Ç
}: {
  action: (fd: FormData) => Promise<void>;
  intervalMs?: number;
}) {
  const formRef = useRef<HTMLFormElement>(null);
  const path = usePathname();

  useEffect(() => {
    const submit = () => {
      const f = formRef.current;
      if (!f) return;
      // submit –±–µ–∑ –ø–µ—Ä–µ–∑–∞–≥—Ä—É–∑–∫–∏/–ø–µ—Ä–µ—Ö–æ–¥–∞
      (f as any).requestSubmit ? (f as any).requestSubmit() : f.submit();
    };

    // –ø—É–ª—å—Å —Å—Ä–∞–∑—É + –ø—Ä–∏ –≤–∏–¥–∏–º–æ—Å—Ç–∏
    submit();
    const onVis = () => { if (document.visibilityState === 'visible') submit(); };

    const t = window.setInterval(() => {
      if (document.visibilityState === 'visible') submit();
    }, intervalMs);

    document.addEventListener('visibilitychange', onVis);
    window.addEventListener('focus', onVis);

    return () => {
      window.clearInterval(t);
      document.removeEventListener('visibilitychange', onVis);
      window.removeEventListener('focus', onVis);
    };
  }, [intervalMs, path]);

  return (
    <form ref={formRef} action={action} style={{ display: 'none' }}>
      {/* –∑–∞–ø–∞—Å–Ω–æ–π –ø–∞—Ä–∞–º–µ—Ç—Ä ‚Äî –≤–¥—Ä—É–≥ –∑–∞—Ö–æ—á–µ—à—å —á—Ç–æ-—Ç–æ –≤–∞—Ä—å–∏—Ä–æ–≤–∞—Ç—å –ø–æ —Ç–µ–∫—É—â–µ–º—É –ø—É—Ç–∏ */}
      <input type="hidden" name="path" value={path || ''} />
    </form>
  );
}

