type Pos = { top: number; left: number };

export default function Tooltip({
  children,
  content,
}: {
  children: ReactNode;
  content: ReactNode;
}) {
  const anchorRef = useRef<HTMLSpanElement | null>(null);
  const [open, setOpen] = useState(false);
  const [pos, setPos] = useState<Pos | null>(null);

  // –ø–µ—Ä–µ—Å—á—ë—Ç –ø–æ–∑–∏—Ü–∏–∏ –æ—Ç–Ω–æ—Å–∏—Ç–µ–ª—å–Ω–æ –æ–∫–Ω–∞
  const compute = () => {
    const el = anchorRef.current;
    if (!el) return;
    const r = el.getBoundingClientRect();

    const GAP = 8; // —Ä–∞—Å—Å—Ç–æ—è–Ω–∏–µ –æ—Ç —è–∫–æ—Ä—è
    let top = r.top - GAP; // –≤—Å–ø–ª—ã–≤–∞–µ–º –≤–≤–µ—Ä—Ö
    let left = r.left + r.width / 2;

    // –µ—Å–ª–∏ –Ω–µ –ø–æ–º–µ—â–∞–µ–º—Å—è —Å–≤–µ—Ä—Ö—É ‚Äî –ø–æ–∫–∞–∑—ã–≤–∞–µ–º —Å–Ω–∏–∑—É
    if (top < 8) top = r.bottom + GAP;

    // –ª—ë–≥–∫–∏–π clamp –ø–æ –≥–æ—Ä–∏–∑–æ–Ω—Ç–∞–ª–∏, —á—Ç–æ–±—ã –Ω–µ –≤—ã–ª–∞–∑–∏–ª –∑–∞ —ç–∫—Ä–∞–Ω
    const vw = window.innerWidth;
    const margin = 8;
    left = Math.min(Math.max(left, margin), vw - margin);

    setPos({ top, left });
  };

  // —Ä–µ–Ω–¥–µ—Ä–∏–º –≤ body –ø–æ—Ä—Ç–∞–ª–æ–º
  const Portal = ({ children }: { children: ReactNode }) => {
    const [mounted, setMounted] = useState(false);
    useEffect(() => {
      setMounted(true);
      return () => setMounted(false);
    }, []);
    if (!mounted) return null;
    return createPortal(children, document.body);
  };

  useLayoutEffect(() => {
    if (!open) return;
    compute();
    const onScroll = () => compute();
    const onResize = () => compute();
    window.addEventListener('scroll', onScroll, true);
    window.addEventListener('resize', onResize);
    return () => {
      window.removeEventListener('scroll', onScroll, true);
      window.removeEventListener('resize', onResize);
    };
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [open]);

  return (
    <>
      <span
        ref={anchorRef}
        style={{ position: 'relative', display: 'inline-flex' }}
        onMouseEnter={() => setOpen(true)}
        onMouseLeave={() => setOpen(false)}
      >
        {children}
      </span>

      {open && pos && (
        <Portal>
          <div
            style={{
              position: 'fixed',
              top: pos.top,
              left: pos.left,
              transform: 'translate(-50%, -100%)', // –ø–æ —É–º–æ–ª—á–∞–Ω–∏—é –Ω–∞–¥ —è–∫–æ—Ä–µ–º
              // –µ—Å–ª–∏ —Ç—É–ª—Ç–∏–ø —Å–Ω–∏–∑—É (top –±—ã–ª —Å–∫–æ—Ä—Ä–µ–∫—Ç–∏—Ä–æ–≤–∞–Ω –Ω–∞ bottom+GAP), –∏–Ω–≤–µ—Ä—Ç–∏—Ä—É–µ–º transform:
              // —Å–¥–µ–ª–∞–µ–º —ç—Ç–æ —á–µ—Ä–µ–∑ –Ω–µ–±–æ–ª—å—à—É—é –ø—Ä–æ–≤–µ—Ä–∫—É:
              ...(anchorRef.current && pos.top > (anchorRef.current.getBoundingClientRect().bottom + 4)
                ? {} // —Å–≤–µ—Ä—Ö—É
                : { transform: 'translate(-50%, 0%)' } // —Å–Ω–∏–∑—É
              ),
              zIndex: 9999, // –Ω–∞–¥ —Å–∞–π–¥–±–∞—Ä–æ–º
              background: 'rgba(255,255,255,.92)',
              backdropFilter: 'saturate(180%) blur(10px)',
              WebkitBackdropFilter: 'saturate(180%) blur(10px)',
              border: '1px solid rgba(229,231,235,.9)',
