import 'server-only';
import { unstable_cache as cache } from 'next/cache';
import { prisma } from '@/lib/prisma';
import type { IndexOptions, SearchItem } from './types';

function norm(s: string | null | undefined): string {
  return (s ?? '').toLowerCase();
}

async function buildIndex(opts: IndexOptions): Promise<SearchItem[]> {
  const limit = opts.limitPerKind ?? 5000;

  const tasks: Promise<SearchItem[]>[] = [];

  if (opts.kinds.includes('user')) {
    tasks.push(
      prisma.user.findMany({
        select: { id: true, name: true, role: true, classroom: true },
        take: limit,
        orderBy: { name: 'asc' },
      }).then(rows =>
        rows.map(r => ({
          id: r.id,
          kind: 'user' as const,
          label: r.name,
          hint: [r.role, r.classroom].filter(Boolean).join(' ‚Ä¢ ') || undefined,
          q: [r.name, r.role, r.classroom].filter(Boolean).join(' '),
        })),
      )
    );
  }

  if (opts.kinds.includes('group')) {
    tasks.push(
      prisma.group.findMany({
        select: { id: true, name: true, _count: { select: { members: true } } },
        take: limit,
        orderBy: { name: 'asc' },
      }).then(rows =>
        rows.map(g => ({
          id: g.id,
          kind: 'group' as const,
          label: g.name,
          hint: g._count.members ? `—É—á–∞—Å—Ç–Ω–∏–∫–æ–≤: ${g._count.members}` : undefined,
          q: g.name,
        })),
      )
    );
  }

  if (opts.kinds.includes('subject')) {
    tasks.push(
      prisma.subject.findMany({
        select: { id: true, name: true, _count: { select: { members: true } } },
        take: limit,
        orderBy: { name: 'asc' },
      }).then(rows =>
        rows.map(s => ({
          id: s.id,
          kind: 'subject' as const,
          label: s.name,
          hint: s._count.members ? `–ø—Ä–µ–ø–æ–¥–∞–≤–∞—Ç–µ–ª–µ–π: ${s._count.members}` : undefined,
          q: s.name,
        })),
      )
    );
  }

  if (opts.kinds.includes('role')) {
    tasks.push(
      prisma.user.findMany({
        select: { role: true },
        where: { role: { not: null } },
        distinct: ['role'],
        orderBy: { role: 'asc' },
        take: limit,
      }).then(rows =>
        rows
          .map(r => r.role!)
          .filter(Boolean)
          .map(role => ({
            id: role,
            kind: 'role' as const,
            label: role,
            q: role,
          } satisfies SearchItem)),
      )
    );
  }

  const parts = await Promise.all(tasks);
  const all = parts.flat();
  return all.map(it => ({ ...it, q: norm(it.q) }));
}

// –í–ê–ñ–ù–û: keyParts ‚Äî –º–∞—Å—Å–∏–≤ —Å—Ç—Ä–æ–∫; –∞—Ä–≥—É–º–µ–Ω—Ç—ã —Ñ—É–Ω–∫—Ü–∏–∏ —Ç–æ–∂–µ –ø–æ–ø–∞–¥–∞—é—Ç –≤ –∫–ª—é—á –∫—ç—à–∞.
// –î–ª—è —É—Å—Ç–æ–π—á–∏–≤–æ—Å—Ç–∏ –∫ –ø–æ—Ä—è–¥–∫—É kinds –∫–∞–Ω–æ–Ω–∏–∑–∏—Ä—É–µ–º –∏—Ö –ø–µ—Ä–µ–¥ —Å–±–æ—Ä–∫–æ–π.
export const getSearchIndex = cache(
  async (opts: IndexOptions) => {
    const kindsSorted = [...opts.kinds].sort() as IndexOptions['kinds'];
    return buildIndex({ ...opts, kinds: kindsSorted });
  },
  ['search-index'],
  { revalidate: 120, tags: ['search-index'] },
);

