.auth-wrap{
  max-width:420px; margin:64px auto; padding:16px; border-radius:16px;
  background:linear-gradient(180deg,var(--glass-top),var(--glass-bottom));
  backdrop-filter:saturate(180%) blur(10px);
  -webkit-backdrop-filter:saturate(180%) blur(10px);
  border:1px solid var(--border);
  box-shadow:var(--glass-shadow), inset 0 1px 0 rgba(255,255,255,.35);
}
.auth-title{ font-size:22px; margin:0 0 12px; font-weight:900; color:var(--text); }
.auth-error{ color:#b91c1c; margin:0 0 10px; }
.auth-form{ display:grid; gap:12px; }
.auth-form label{ display:grid; gap:6px; }
.auth-form input{
  padding:10px 12px; border:1px solid rgba(229,231,235,.9);
  border-radius:10px; background:#fff; outline:none;
}
.auth-form input:focus{
  border-color:#cfe3ff; box-shadow:0 0 0 3px rgba(207,227,255,.4);
}
.auth-form button{
  height:36px; border:1px solid var(--brand); border-radius:10px;
  background:var(--brand); color:#fff;
}
.auth-form button:hover{ filter:brightness(.96); }

